import axios from 'axios';

const API_URL = 'http://localhost:8000/api/properties/';

// Función auxiliar para obtener el token de autenticación
const getAuthToken = () => {
  return localStorage.getItem('authToken');
};

const propertyService = {
  getAllProperties: async () => {
    try {
      const response = await axios.get(API_URL);
      return response.data;
    } catch (error) {
      console.error('Error fetching properties:', error);
      throw error;
    }
  },
  
  getPropertyById: async (id) => {
    try {
      // Add authentication token to the request
      const token = getAuthToken();
      
      const config = {
        headers: {
          'Authorization': token ? `Token ${token}` : ''
        }
      };
      
      console.log(`Requesting property with ID: ${id} from ${API_URL}${id}/`);
      const response = await axios.get(`${API_URL}${id}/`, config);
      console.log('API Response:', response);
      
      return response.data;
    } catch (error) {
      console.error(`Error fetching property with id ${id}:`, error);
      if (error.response) {
        console.error('Response data:', error.response.data);
        console.error('Response status:', error.response.status);
      }
      throw error;
    }
  },
  
  getFeaturedProperties: async () => {
    try {
      const response = await axios.get(`${API_URL}featured/`);
      return response.data;
    } catch (error) {
      console.error('Error fetching featured properties:', error);
      throw error;
    }
  },
  
  searchProperties: async (params) => {
    try {
      const response = await axios.get(`${API_URL}search/`, { params });
      return response.data;
    } catch (error) {
      console.error('Error searching properties:', error);
      throw error;
    }
  },
  
  createProperty: async (propertyData) => {
    try {
      // Obtener token de autenticación
      const token = getAuthToken();
      
      const config = {
        headers: {
          'Content-Type': propertyData instanceof FormData ? 'multipart/form-data' : 'application/json',
          'Authorization': token ? `Token ${token}` : ''
        },
      };
      
      const response = await axios.post(API_URL, propertyData, config);
      return response.data;
    } catch (error) {
      console.error('Error creating property:', error);
      throw error;
    }
  },
  
  // Update property with admin override option
  updateProperty: async (id, formData, isAdmin = false) => {
    try {
      // If not admin, use standard token authentication
      if (!isAdmin) {
        const token = getAuthToken();
        if (!token) {
          throw new Error('No authentication token found');
        }
        
        const config = {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Token ${token}`
          }
        };
        
        const response = await axios.put(`${API_URL}${id}/`, formData, config);
        return response.data;
      } else {
        // For admin users, use admin override
        const config = {
          headers: {
            'Content-Type': 'multipart/form-data',
            'X-Admin-Override': 'true'
          }
        };
        
        const response = await axios.put(`${API_URL}${id}/`, formData, config);
        return response.data;
      }
    } catch (error) {
      console.error('Error updating property:', error);
      throw error;
    }
  },
  
  deleteProperty: async (id) => {
    try {
      // Obtener token de autenticación
      const token = getAuthToken();
      
      const config = {
        headers: {
          'Authorization': token ? `Token ${token}` : ''
        }
      };
      
      const response = await axios.delete(`${API_URL}${id}/`, config);
      return response.data;
    } catch (error) {
      console.error(`Error deleting property with id ${id}:`, error);
      throw error;
    }
  }
};

export default propertyService;
