import React, { createContext, useContext, useState, useEffect } from 'react';

// Valores predeterminados de configuración
const defaultConfig = {
  authRequired: true,
  imageProcessing: {
    keepOriginal: true,
    compressionLevel: 'medium',
    maxSize: 5 * 1024 * 1024 // 5MB
  },
  apiEndpoints: {
    properties: 'http://localhost:8000/api/properties/',
    auth: 'http://localhost:8000/api/auth/'
  },
  formValidation: {
    strictMode: false,
    validateOnChange: true
  },
  systemCode: '10020001' // Código de sistema por defecto
};

const ConfigContext = createContext();

export const ConfigProvider = ({ children }) => {
  const [config, setConfig] = useState(() => {
    // Intentar cargar configuración desde localStorage
    const savedConfig = localStorage.getItem('appConfig');
    return savedConfig ? JSON.parse(savedConfig) : defaultConfig;
  });
  
  // Guardar configuración en localStorage cuando cambie
  useEffect(() => {
    localStorage.setItem('appConfig', JSON.stringify(config));
  }, [config]);
  
  // Función para restablecer toda la configuración
  const resetConfig = () => {
    setConfig(defaultConfig);
    return defaultConfig;
  };
  
  // Función para actualizar una configuración específica
  const updateConfig = (section, values) => {
    setConfig({
      ...config,
      [section]: {
        ...config[section],
        ...values
      }
    });
  };
  
  // Función para generar un código de sistema basado en el estado actual
  const generateSystemCode = () => {
    const isAuthenticated = !!localStorage.getItem('authToken');
    const environment = process.env.NODE_ENV === 'production' ? '2' : '1';
    const component = '10'; // Frontend
    const category = isAuthenticated ? '01' : '02'; // Auth o Properties
    const specificCode = isAuthenticated ? '001' : '002';
    
    const systemCode = `${environment}${component}${category}${specificCode}`;
    
    // Actualizar el código en la configuración
    setConfig({
      ...config,
      systemCode
    });
    
    return systemCode;
  };
  
  return (
    <ConfigContext.Provider 
      value={{ 
        config, 
        resetConfig, 
        updateConfig,
        generateSystemCode
      }}
    >
      {children}
    </ConfigContext.Provider>
  );
};

export const useConfig = () => useContext(ConfigContext);
