import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt, faBed, faBath, faRulerCombined, faSearch } from '@fortawesome/free-solid-svg-icons';
import propertyService from '../services/propertyService';
import PropertyCard from '../components/PropertyCard';
import { Helmet } from 'react-helmet-async';
import './Properties.css';

const Properties = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Filter states
  const [filters, setFilters] = useState({
    operation_type: '',
    min_price: '',
    max_price: '',
    bedrooms: '',
    property_type: ''
  });

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const data = await propertyService.getAllProperties();
      setProperties(data);
      setError(null);
    } catch (err) {
      console.error('Error fetching properties:', err);
      setError('Error al cargar las propiedades. Por favor, intente de nuevo más tarde.');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };

  const applyFilters = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      // Remove empty filters
      const activeFilters = Object.fromEntries(
        Object.entries(filters).filter(([_, value]) => value !== '')
      );
      
      const data = await propertyService.searchProperties(activeFilters);
      setProperties(data);
      setError(null);
    } catch (err) {
      console.error('Error searching properties:', err);
      setError('Error al buscar propiedades. Por favor, intente de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  const clearFilters = () => {
    setFilters({
      operation_type: '',
      min_price: '',
      max_price: '',
      bedrooms: '',
      property_type: ''
    });
    fetchProperties();
  };

  return (
    <div className="properties-page">
      <Helmet>
        <title>Propiedades | Bienes Raíces</title>
        <meta name="description" content="Explora nuestra selección de propiedades en venta y alquiler" />
      </Helmet>
      
      <div className="page-header">
        <h1>Nuestras Propiedades</h1>
      </div>
      
      <div className="operation-tabs">
        <button 
          className={filters.operation_type === 'Sale' ? 'active' : ''} 
          onClick={() => setFilters({...filters, operation_type: 'Sale'})}
        >
          Venta
        </button>
        <button 
          className={filters.operation_type === 'Rent' ? 'active' : ''} 
          onClick={() => setFilters({...filters, operation_type: 'Rent'})}
        >
          Alquiler
        </button>
        <button 
          className={filters.operation_type === 'Lease' ? 'active' : ''} 
          onClick={() => setFilters({...filters, operation_type: 'Lease'})}
        >
          Arrendamiento
        </button>
      </div>
      
      <div className="properties-container">
        <div className="filters-sidebar">
          <h3>Filtros</h3>
          <form onSubmit={applyFilters}>
            <div className="filter-group">
              <label htmlFor="property_type">Tipo de Propiedad</label>
              <select 
                id="property_type" 
                name="property_type" 
                value={filters.property_type}
                onChange={handleFilterChange}
              >
                <option value="">Todos</option>
                <option value="House">Casa</option>
                <option value="Apartment">Apartamento</option>
                <option value="Commercial">Comercial</option>
                <option value="Land">Terreno</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>Rango de Precio Mensual ($)</label>
              <div className="price-range">
                <input 
                  type="number" 
                  name="min_price" 
                  placeholder="0" 
                  value={filters.min_price}
                  onChange={handleFilterChange}
                />
                <span>-</span>
                <input 
                  type="number" 
                  name="max_price" 
                  placeholder="1000000" 
                  value={filters.max_price}
                  onChange={handleFilterChange}
                />
              </div>
            </div>
            
            <div className="filter-group">
              <label htmlFor="bedrooms">Habitaciones (mínimo)</label>
              <select 
                id="bedrooms" 
                name="bedrooms" 
                value={filters.bedrooms}
                onChange={handleFilterChange}
              >
                <option value="">Cualquier cantidad</option>
                <option value="1">1+</option>
                <option value="2">2+</option>
                <option value="3">3+</option>
                <option value="4">4+</option>
                <option value="5">5+</option>
              </select>
            </div>
            
            <div className="filter-buttons">
              <button type="submit" className="apply-filters-btn">
                <FontAwesomeIcon icon={faSearch} /> Aplicar Filtros
              </button>
              <button type="button" className="clear-filters-btn" onClick={clearFilters}>
                Limpiar Filtros
              </button>
            </div>
          </form>
        </div>
        
        <div className="properties-list">
          {loading ? (
            <div className="loading-message">Cargando propiedades...</div>
          ) : error ? (
            <div className="error-message">{error}</div>
          ) : properties.length === 0 ? (
            <div className="no-results">
              No se encontraron propiedades con los filtros seleccionados.
            </div>
          ) : (
            <div className="properties-grid">
              {properties.map(property => (
                <PropertyCard key={property.id} property={property} />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Properties;
