import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import propertyService from '../services/propertyService';
import '../styles/Properties.css';

const Properties = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const data = await propertyService.getAllProperties();
        setProperties(data);
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError('Error al cargar las propiedades. Por favor, intente de nuevo más tarde.');
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (loading) {
    return <div className="loading">Cargando propiedades...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="properties-page">
      <div className="container">
        <h1>Nuestras Propiedades</h1>
        
        {properties.length === 0 ? (
          <p className="no-properties">No hay propiedades disponibles en este momento.</p>
        ) : (
          <div className="properties-grid">
            {properties.map(property => (
              <div key={property.id} className="property-card">
                <div className="property-image">
                  {property.image ? (
                    <img src={property.image} alt={property.title} />
                  ) : (
                    <div className="no-image">Sin imagen</div>
                  )}
                </div>
                <div className="property-info">
                  <h3>{property.title}</h3>
                  <p className="property-location">{property.location}</p>
                  <p className="property-price">${property.price.toLocaleString()}</p>
                  <Link to={`/properties/${property.id}`} className="view-details">
                    Ver detalles
                  </Link>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Properties;
