import axios from 'axios';

const API_URL = 'http://localhost:8000/api/auth/';

const authService = {
  login: async (credentials) => {
    try {
      const response = await axios.post(`${API_URL}login/`, credentials);
      if (response.data.token) {
        localStorage.setItem('authToken', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
      }
      return response.data;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  },

  logout: () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('user');
  },

  getCurrentUser: () => {
    const user = localStorage.getItem('user');
    return user ? JSON.parse(user) : null;
  },

  // Add this method to check if the token is valid
  isTokenValid: () => {
    const token = localStorage.getItem('authToken');
    if (!token) return false;
    
    // For a real app, you would verify the token's expiration
    // For this demo, we'll just check if it exists and has a reasonable length
    return token.length > 10;
  },
  
  // Update the isAuthenticated method to use the token validation
  isAuthenticated: () => {
    return authService.isTokenValid();
  },
  
  // Add a method to refresh the token if needed
  refreshToken: async () => {
    // In a real app, this would call an API endpoint to refresh the token
    // For this demo, we'll just extend the token's "lifetime"
    const token = localStorage.getItem('authToken');
    if (token) {
      // Create a "refreshed" token by adding a timestamp
      const refreshedToken = token + '_refreshed_' + Date.now();
      localStorage.setItem('authToken', refreshedToken);
      return true;
    }
    return false;
  },  // Added comma here
  
  // For demo purposes - creates a fake token
  demoLogin: (email, password) => {
    // This is just for development/testing
    if (email === 'admin@bienesraices.com' && password === 'admin123') {
      const fakeToken = 'demo-token-' + Math.random().toString(36).substring(2);
      localStorage.setItem('authToken', fakeToken);
      localStorage.setItem('user', JSON.stringify({
        name: 'Administrador',
        email: email,
        role: 'admin'
      }));
      return true;
    }
    return false;
  }
};

export default authService;
