import axios from 'axios';

// Cambiamos la URL base para la autenticación
const API_URL = 'http://localhost:8000/api-auth/';
// URL base para otras operaciones de API
const BASE_API_URL = 'http://localhost:8000/api/';

// Configurar axios para manejar CSRF
axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = true; // Importante para enviar cookies en solicitudes cross-origin

// Check if the current user is an admin
export const isAdminUser = async () => {
  try {
    // First check for a special admin flag in localStorage
    const adminFlag = localStorage.getItem('isAdminUser');
    if (adminFlag === 'true') {
      return true;
    }
    
    // Then check with the server if we have a token
    const token = localStorage.getItem('authToken');
    if (token) {
      const response = await axios.get(`${API_URL}check-admin/`, {
        headers: {
          'Authorization': `Token ${token}`
        }
      });
      
      if (response.status === 200) {
        // Store the admin status for future checks
        localStorage.setItem('isAdminUser', response.data.isAdmin.toString());
        return response.data.isAdmin;
      }
    }
    
    return false;
  } catch (error) {
    console.error('Error checking admin status:', error);
    return false;
  }
};

const authService = {
  // Obtener información del usuario actual
  getCurrentUser: async () => {
    try {
      const token = localStorage.getItem('authToken');
      
      if (!token) {
        return null;
      }
      
      const config = {
        headers: {
          'Authorization': `Token ${token}`
        }
      };
      
      const response = await axios.get(`${API_URL}user/`, config);
      return response.data;
    } catch (error) {
      console.error('Error fetching current user:', error);
      return null;
    }
  },
  
  // Verificar si el usuario está autenticado
  isAuthenticated: () => {
    return !!localStorage.getItem('authToken');
  },
  
  // Cerrar sesión
  logout: () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('isAdminUser');
  },
  
  // Login function
  login: async (credentials) => {
    try {
      console.log('Intentando iniciar sesión con:', credentials);
      
      // Primero, obtener el token CSRF
      await axios.get(`${API_URL}`);
      
      // Luego hacer la solicitud de login
      const response = await axios.post(`${API_URL}login/`, credentials);
      console.log('Respuesta del servidor:', response.data);
      
      if (response.data.token) {
        localStorage.setItem('authToken', response.data.token);
        
        // Check if user is admin and store that info
        if (response.data.user && response.data.user.is_staff) {
          localStorage.setItem('isAdminUser', 'true');
        }
      }
      return response.data;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  },
  
  // Register function
  register: async (userData) => {
    try {
      console.log('Enviando datos de registro al servidor:', userData);
      
      // Primero, obtener el token CSRF
      await axios.get(`${API_URL}`);
      
      // Luego hacer la solicitud de registro
      const response = await axios.post(`${API_URL}register/`, userData);
      
      console.log('Respuesta del servidor para registro:', response.data);
      return response.data;
    } catch (error) {
      console.error('Registration error details:', error.response?.data);
      throw error;
    }
  }
};

export default authService;
