          bedrooms: 0,
          bathrooms: 0,
          area: 300,
          image: null,
          featured: true
        }
      ];
      
      // Uncomment this code when backend is ready:
      /*
      const response = await axios.get(`${API_URL}properties/`);
      return response.data;
      */
      
      return mockData;
    } catch (error) {
      console.error('Error fetching properties:', error);
      throw error;
    }
  },
  
  // Get property by ID
  getPropertyById: async (id) => {
    try {
      // Uncomment this code when backend is ready:
      /*
