        {
          id: 2,
          title: 'Edificio Osiris',
          description: 'Edificio comercial en zona céntrica',
          price: 500000,
          location: 'Ensanche la Paz, Santo Domingo, R. D.',
          bedrooms: 0,
          bathrooms: 0,
          area: 300,
          image: null,
          featured: true
        }
      ];
      
      const property = mockProperties.find(p => p.id === parseInt(id));
      if (!property) {
        throw new Error('Property not found');
      }
      return property;
    } catch (error) {
      console.error(`Error fetching property ${id}:`, error);
      throw error;
    }
  },
  
  // Create new property
  createProperty: async (propertyData) => {
    try {
      // Uncomment this code when backend is ready:
      /*
      const headers = {
        ...getAuthHeader(),
        'Content-Type': 'multipart/form-data'
