      return response.data;
      */
      
      // For now, just log and return mock data
      console.log('Creating property with data:', propertyData);
      return {
        id: 3,
        ...propertyData
      };
    } catch (error) {
      console.error('Error creating property:', error);
      throw error;
    }
  },
  
  // Update property
