import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import propertyService from '../services/propertyService';
import { useConfig } from '../context/ConfigContext';
import './PropertyForm.css';

const PropertyEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  
  // Use optional chaining to safely access config values
  const configContext = useConfig();
  const { config, generateSystemCode } = configContext || {};
  
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formError, setFormError] = useState(null);
  const [property, setProperty] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    property_type: 'House',
    operation_type: 'Sale',
    bedrooms: 0,
    bathrooms: 0,
    area: 0,
    is_featured: false,
    image: null
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [currentImage, setCurrentImage] = useState(null);
  const [imageChanged, setImageChanged] = useState(false);
  const [systemStatus, setSystemStatus] = useState(null);

  // Check authentication on component mount
  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    if (!authToken) {
      setFormError('Error de autenticación: Debe iniciar sesión para editar propiedades.');
      setLoading(false); // Make sure to set loading to false when there's an auth error
      return;
    }
    
    // Load property data when component mounts and user is authenticated
    const fetchProperty = async () => {
      try {
        setLoading(true);
        console.log(`Fetching property with ID: ${id}`);
        
        const data = await propertyService.getPropertyById(id);
        console.log('Property data received:', data);
        
        if (!data || Object.keys(data).length === 0) {
          throw new Error('No se recibieron datos de la propiedad');
        }
        
        setProperty({
          ...data,
          // Convert numeric values to strings for form inputs
          price: data.price?.toString() || '',
          bedrooms: data.bedrooms?.toString() || '0',
          bathrooms: data.bathrooms?.toString() || '0',
          area: data.area?.toString() || ''
        });
        
        if (data.image) {
          setCurrentImage(data.image);
        }
        setError(null);
      } catch (err) {
        console.error('Error fetching property:', err);
        setError(`Error al cargar la propiedad: ${err.message || 'Por favor, intente de nuevo.'}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProperty({
      ...property,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  // Handle image file selection
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProperty({
        ...property,
        image: file
      });
      setImageChanged(true);
      
      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setFormError(null);
    
    try {
      // Generate system code if available
      const currentSystemCode = generateSystemCode ? generateSystemCode() : 'system-code-unavailable';
      setSystemStatus(currentSystemCode);
      
      // Create FormData object to handle file upload
      const formData = new FormData();
      
      // Add all property fields to FormData
      Object.keys(property).forEach(key => {
        if (key === 'image') {
          // Only append image if it's a new file
          if (imageChanged && property[key] instanceof File) {
            // Check max size if config is available
            const maxSize = config?.imageProcessing?.maxSize || 5 * 1024 * 1024;
            if (property[key].size > maxSize) {
              throw new Error(`La imagen excede el tamaño máximo permitido de ${maxSize / (1024 * 1024)}MB`);
            }
            formData.append('image', property[key]);
          }
        } else {
          // Make sure we're not sending undefined or null values
          if (property[key] !== null && property[key] !== undefined) {
            formData.append(key, property[key]);
          }
        }
      });
      
      // If we're not changing the image, we need to tell the backend to keep the existing one
      if (!imageChanged && currentImage) {
        formData.append('keep_image', 'true');
      }
      
      // Check if user is authenticated
      const authToken = localStorage.getItem('authToken');
      if (!authToken) {
        setFormError('Error de autenticación: Debe iniciar sesión para editar propiedades.');
        setLoading(false);
        // Redirect to login
        navigate('/login', { state: { from: { pathname: `/admin/properties/edit/${id}` } } });
        return;
      }
      
      // Complete the submission logic by actually calling the update method
      const updatedProperty = await propertyService.updateProperty(id, formData);
      console.log('Property updated successfully:', updatedProperty);
      
      // Redirect to property list after successful update
      navigate('/admin/properties');
      
    } catch (err) {
      console.error('Error updating property:', err);
      
      // Handle authentication errors specifically
      if (err.response && err.response.status === 401) {
        setFormError('Error de autenticación: Sus credenciales han expirado. Por favor, inicie sesión nuevamente.');
        // Clear invalid token
        localStorage.removeItem('authToken');
        // Redirect to login
        setTimeout(() => {
          navigate('/login', { state: { from: { pathname: `/admin/properties/edit/${id}` } } });
        }, 2000);
      } else {
        setFormError(`Error al actualizar la propiedad: ${err.response?.data?.detail || err.message || 'Por favor, intente de nuevo.'}`);
      }
    } finally {
      setLoading(false);
    }
  };

  if (loading && !property.id) {
    return (
      <div className="loading-container">
        <div className="loading">Cargando propiedad...</div>
        <p>Si la carga tarda demasiado, <button onClick={() => navigate('/admin/properties')} className="text-button">volver a la lista</button></p>
      </div>
    );
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="property-form-container">
      <h1>Editar Propiedad</h1>
      
      {formError && <div className="error-message">{formError}</div>}
      
      <form onSubmit={handleSubmit} className="property-form">
        <div className="form-group">
          <label htmlFor="title">Título *</label>
          <input
            type="text"
            id="title"
            name="title"
            value={property.title || ''}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="description">Descripción *</label>
          <textarea
            id="description"
            name="description"
            value={property.description || ''}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="price">Precio *</label>
            <input
              type="number"
              id="price"
              name="price"
              value={property.price || ''}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="location">Ubicación *</label>
            <input
              type="text"
              id="location"
              name="location"
              value={property.location || ''}
              onChange={handleChange}
              required
            />
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="property_type">Tipo de Propiedad *</label>
            <select
              id="property_type"
              name="property_type"
              value={property.property_type || 'House'}
              onChange={handleChange}
              required
            >
              <option value="House">Casa</option>
              <option value="Apartment">Apartamento</option>
              <option value="Commercial">Comercial</option>
              <option value="Land">Terreno</option>
            </select>
          </div>
          
          <div className="form-group">
            <label htmlFor="operation_type">Tipo de Operación *</label>
            <select
              id="operation_type"
              name="operation_type"
              value={property.operation_type || 'Sale'}
              onChange={handleChange}
              required
            >
              <option value="Sale">Venta</option>
              <option value="Rent">Alquiler</option>
              <option value="Lease">Arrendamiento</option>
            </select>
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="bedrooms">Habitaciones</label>
            <input
              type="number"
              id="bedrooms"
              name="bedrooms"
              value={property.bedrooms || '0'}
              onChange={handleChange}
              min="0"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="bathrooms">Baños</label>
            <input
              type="number"
              id="bathrooms"
              name="bathrooms"
              value={property.bathrooms || '0'}
              onChange={handleChange}
              min="0"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="area">Área (m²) *</label>
            <input
              type="number"
              id="area"
              name="area"
              value={property.area || ''}
              onChange={handleChange}
              required
              min="1"
            />
          </div>
        </div>
        
        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="is_featured"
            name="is_featured"
            checked={property.is_featured || false}
            onChange={handleChange}
          />
          <label htmlFor="is_featured">Destacar esta propiedad</label>
        </div>
        
        <div className="form-group">
          <label htmlFor="image">Imagen</label>
          <input
            type="file"
            id="image"
            name="image"
            onChange={handleImageChange}
            accept="image/*"
          />
          {(imagePreview || currentImage) && (
            <div className="image-preview">
              <img 
                src={imagePreview || currentImage} 
                alt="Vista previa" 
                style={{ maxWidth: '100%', maxHeight: '200px' }} 
              />
            </div>
          )}
        </div>
        
        <div className="form-actions">
          <button type="button" onClick={() => navigate('/admin/properties')} className="cancel-btn">
            Cancelar
          </button>
          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Actualizando...' : 'Actualizar Propiedad'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default PropertyEdit;
